package program;

public class StringArrDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String cars[] = {"Audi", "Nexon","Jaguar", "RangeRover" };
		
		char ch[] = {'a', 'c', 'b', 'd'};
		
		for(int i=0; i<=3  ; i++)
		{
			System.out.println(cars[i]);
			System.out.println(ch[i]);
		}
	}

}
